import React, { useState } from 'react';

const VotingSystem = () => {
  const [votes, setVotes] = useState({ Alice: 0, Bob: 0, Carol: 0 });

  const handleVote = (candidate) => {
    setVotes(prevVotes => ({ ...prevVotes, [candidate]: prevVotes[candidate] + 1 }));
  };

  const handleSubmit = () => {
    alert('Thanks for your votes!');
  };

  return (
    <div className='container'>
      <div className='candidate-list'>
        {Object.keys(votes).map((candidate, index) => (
          <div key={index} className='candidate-item'>
            <p>{candidate}</p>
            <button onClick={() => handleVote(candidate)} className='vote-button'>Vote for {candidate}</button>
          </div>
        ))}
      </div>
      <div className='results-chart'>
        {Object.entries(votes).map(([candidate, count], index) => (
          <div key={index}>
            <p>{candidate}: {count} votes</p>
          </div>
        ))}
      </div>
      <button onClick={handleSubmit} className='submit-button'>Submit Votes</button>
    </div>
  );
};

export default VotingSystem;

