import React, { useState } from 'react';

const PlantCareDiary = () => {
  const [logEntry, setLogEntry] = useState('');
  const [logs, setLogs] = useState([]);
  const [careTips, setCareTips] = useState('Make sure to water the plants at least once a week, and be careful not to let water accumulate.');

  const handleAddLog = () => {
    const newLogs = [...logs, logEntry];
    setLogs(newLogs);
    setLogEntry('');
    updateCareTips();
  };

  const updateCareTips = () => {
    // Update care instructions based on the type of plant and its maintenance history.
    setCareTips('Consider adjusting the frequency of watering and fertilizing according to the current season.');
  };

  return (
    <div className='container'>
      <div className='input-area'>
        <textarea className='input-field' placeholder='Record maintenance activities such as watering and fertilizing.' value={logEntry} onChange={(e) => setLogEntry(e.target.value)} />
        <button className='submit-button' onClick={handleAddLog}>Add record</button>
      </div>
      <div className='log-list'>
        {logs.map((log, index) => (
          <div key={index}>{log}</div>
        ))}
      </div>
