import React, { useState } from 'react';

const BookNotesApp = () => {
  const [selectedBook, setSelectedBook] = useState('');
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');

  const handleNoteAdd = () => {
    setNotes([...notes, newNote]);
    setNewNote('');
  };

  return (
    <div className='container'>
      <select className='book-select' value={selectedBook} onChange={(e) => setSelectedBook(e.target.value)}>
        <option value=''>Select Book</option>
        <option value='book1'>book 1</option>
        <option value='book2'>book 2</option>
      </select>
      <textarea className='note-input' placeholder='Please enter your reading notes here.' value={newNote} onChange={(e) => setNewNote(e.target.value)} />
      <button onClick={handleNoteAdd} className='submit-button'>Add note</button>
      <div className='note-list'>
        {notes.map((note, index) => (
          <div key={index} className='note-item'>
            <span>{note}</span>
            <button className='edit-button'>Edit</button>
            <button className='delete-button' onClick={() => setNotes(notes.filter((_, i) => i !== index))}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookNotesApp;

