import React, { useState } from 'react';

const EventRegistrationApp = () => {
  const [events, setEvents] = useState([{ name: 'Technical Meeting', description: 'Exploring the latest cloud computing technologies...', date: '2021-09-10' }, { name: 'Photography Workshop', description: 'Photography Workshop', date: '2021-09-20' }]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [registrantInfo, setRegistrantInfo] = useState({ name: '', email: '', phone: '' });

  const handleEventSelect = (event) => {
    setSelectedEvent(event);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setRegistrantInfo(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    alert('Registration Success!');
  };

  return (
    <div className='container'>
      <div className='event-list'>
        {events.map((event, index) => (
          <div key={index} onClick={() => handleEventSelect(event)}>{event.name}</div>
        ))}
      </div>
      <div className='event-details'>
        {selectedEvent ? (
          <div>
            <h2>{selectedEvent.name}</h2>
            <p>{selectedEvent.description}</p>
            <p>Date: {selectedEvent.date}</p>
          </div>
        ) : <p>Please select an activity</p>}
      </div>
      <div className='registration-form'>
        <input type='text' name='name' placeholder='name' className='input-field' value={registrantInfo.name} onChange={handleInputChange} />
        <input type='email' name='email' placeholder='email' className='input-field' value={registrantInfo.email} onChange={handleInputChange} />
        <input type='text' name='phone' placeholder='phone' className='input-field' value={registrantInfo.phone} onChange={handleInputChange} />
        <button className='submit-button' onClick={handleSubmit}>register</button>
      </div>
    </div>
  );
};

export default EventRegistrationApp;

