import React, { useState } from 'react';

const RestaurantBookingSystem = () => {
  const [selectedTable, setSelectedTable] = useState('');
  const [bookingDetails, setBookingDetails] = useState({ date: '', time: '', guests: 0 });
  const [confirmation, setConfirmation] = useState(false);

  const handleTableSelect = (table) => {
    setSelectedTable(table);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingDetails({ ...bookingDetails, [name]: value });
  };

  const handleSubmit = () => {
    setConfirmation(true);
  };

  return (
    <div className='container'>
      <div className='seating-chart' onClick={() => handleTableSelect('Table 5')}>click to select seat</div>
      <div className='input-form'>
        <input type='date' name='date' className='input-field' onChange={handleInputChange} />
        <input type='time' name='time' className='input-field' onChange={handleInputChange} />
        <input type='number' name='guests' placeholder='guests' className='input-field' onChange={handleInputChange} />
      </div>
      <div className='confirm-section'>
        {confirmation ? <p>Reservation successful! Table number: {selectedTable}, date: {bookingDetails.date}, time: {bookingDetails.time}, guest number: {bookingDetails.guests}</p> : 'Please confirm and submit your order.'}
        <button className='submit-button' onClick={handleSubmit}>Submit Reservation</button>
      </div>
    </div>
  );
};

export default RestaurantBookingSystem;

