import React, { useState } from 'react';

const HealthManagementSystem = () => {
  const [healthData, setHealthData] = useState({ heartRate: 72, bloodPressure: '120/80' });
  const [healthLogs, setHealthLogs] = useState([]);
  const [healthAdvice, setHealthAdvice] = useState('Based on your records, it is recommended to walk at least 8,000 steps per day.');

  const addHealthLog = (log) => {
    setHealthLogs([...healthLogs, log]);
    setHealthAdvice('Please ensure to maintain adequate water intake.');
  };

  return (
    <div className='container'>
      <div className='monitoring-section'>
        heart beat rate: {healthData.heartRate} BPM
        blood presure: {healthData.bloodPressure}
      </div>
      <div className='log-section'>
        <input type='text' className='input-field' placeholder='enter activity record' />
        <button className='submit-button' onClick={() => addHealthLog('walk for 8,000 steps')}>add record</button>
        {healthLogs.map((log, index) => <div key={index}>{log}</div>)}
      </div>
      <div className='advice-section'>
        {healthAdvice}
      </div>
    </div>
  );
};

export default HealthManagementSystem;

