import React, { useState } from 'react';

const TaskForm = () => {
  const [task, setTask] = useState({ description: '', dueDate: '', priority: '' });
  const handleSubmit = (event) => {
    event.preventDefault();
    if (!task.description || !task.dueDate || !task.priority) {
      alert('please fill in all blanks');
    } else {
      alert('task added: ' + task.description);
      setTask({ description: '', dueDate: '', priority: '' }); // Reset form
    }
  };

  return (
    <div className='form-container' style={{ fontFamily: 'Roboto', backgroundColor: '#E3F2FD', color: '#0D47A1' }}> 
      <input type='text' placeholder='task description' value={task.description} onChange={e => setTask({ ...task, description: e.target.value })} className='input' />
      <input type='date' value={task.dueDate} onChange={e => setTask({ ...task, dueDate: e.target.value })} className='input' />
      <select value={task.priority} onChange={e => setTask({ ...task, priority: e.target.value })} className='selector'>
        <option value=''>select priority</option>
        <option value='High'>high</option>
        <option value='Medium'>middle</option>
