import React, { useState } from 'react';

const recipes = [
  { id: 1, title: 'Local Stew', image: 'path/to/image1.jpg', ingredients: ['Onions', 'Tomatoes', 'Beef'], instructions: 'Cook all ingredients for 2 hours.' },
  { id: 2, title: 'Traditional Pie', image: 'path/to/image2.jpg', ingredients: ['Flour', 'Butter', 'Apples'], instructions: 'Bake the pie for 45 minutes.' },
  { id: 3, title: 'Community Soup', image: 'path/to/image3.jpg', ingredients: ['Carrots', 'Potatoes', 'Chicken'], instructions: 'Simmer on low heat for 3 hours.' }
];

const RecipeBook = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextRecipe = () => setCurrentIndex((currentIndex + 1) % recipes.length);
