        <img src={recipes[currentIndex].image} alt={recipes[currentIndex].title} className='recipe-image' />
        <h3>{recipes[currentIndex].title}</h3>
        <p>Ingredients: {recipes[currentIndex].ingredients.join(', ')}</p>
        <p>Instructions: {recipes[currentIndex].instructions}</p>
      </div>
      <button className='nav-button' onClick={nextRecipe}>&gt;</button>
    </div>
  );
};

export default RecipeBook;

