component:
import React, { useState, useEffect } from 'react';

const eventInfo = {
  name: 'Community Festival',
  description: 'Join us for the annual community festival with food, music, and games!',
  dateTime: '2024-10-01T12:00:00'
};

const CountdownWidget = () => {
  const [timeLeft, setTimeLeft] = useState('');

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const eventDate = new Date(eventInfo.dateTime);
      const difference = eventDate - now;
      const days = Math.floor(difference / (1000 * 60 * 60 * 24));
      const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
      const minutes = Math.floor((difference / 1000 / 60) % 60);
      const seconds = Math.floor((difference / 1000) % 60);
      setTimeLeft(`${days} days ${hours} hours ${minutes} minutes ${seconds} seconds`);
      if (difference < 0) {
        clearInterval(timer);
        setTimeLeft('Event has started!');
      }
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className='countdown-container'>
      <div className='event-name'>{eventInfo.name}</div>
      <div className='event-description'>{eventInfo.description}</div>
      <div className='timer'>{timeLeft}</div>
    </div>
  );
};

export default CountdownWidget;

