import React, { useState } from 'react';

const TodoList = ({ tasks, onDelete }) => {
  return (
    <div className="todo-list">
      <h2>To-Do List</h2>
      {tasks.map((task, index) => (
        <div key={index} className="todo-item">
          {task}
          <button className="delete-button" onClick={() => onDelete(index)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (task) {
      setTasks([...tasks, task]);
      setTask('');
    }
  };

  const handleDeleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  return (
    <div>
      <div className="header">
        <h1>To-Do List Application</h1>
      </div>
      <form className="input-area" onSubmit={handleAddTask}>
        <input
          type="text"
          className="input"
          placeholder="Add a new task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          required
        />
        <button className="add-button">Add</button>
      </form>
      <TodoList tasks={tasks} onDelete={handleDeleteTask} />
      <div className="footer">
        <p>&copy; 2024 Todo Application</p>
      </div>
    </div>
  );
};

export default App;

