import React, { useState, useEffect } from 'react';

const BusTracker = () => {
  const [selectedRoute, setSelectedRoute] = useState('');
  const [busLocation, setBusLocation] = useState(null);

  useEffect(() => {
    // Fetch bus location based on selectedRoute
    const fetchLocation = async () => {
      const response = await fetch(`/api/location/${selectedRoute}`);
      const data = await response.json();
      setBusLocation(data);
    };
    if (selectedRoute) {
      fetchLocation();
    }
  }, [selectedRoute]);

  return (
    <div>
      <div className="selector-area">
        <select className="route-selector" onChange={(e) => setSelectedRoute(e.target.value)}>
          <option value="">Select a Route</option>
          <option value="route1">Route 1</option>
          <option value="route2">Route 2</option>
          <option value="route3">Route 3</option>
        </select>
      </div>
      <div className="map-area">
        {busLocation ? <p>Bus Location: {busLocation.latitude}, {busLocation.longitude}</p> : <p>No route selected.</p>}
      </div>
    </div>
  );
};

export default BusTracker;

