import React, { useState } from 'react';

const TextEditor = () => {
  const [text, setText] = useState('');
  const [format, setFormat] = useState({ bold: false, italic: false, underline: false });

  const toggleFormat = (key) => {
    setFormat({ ...format, [key]: !format[key] });
  };

  const formattedText = () => {
    let style = '';
    if (format.bold) style += 'font-weight:bold;';
    if (format.italic) style += 'font-style:italic;';
    if (format.underline) style += 'text-decoration:underline;';
    return <div style={{ style }}>{text}</div>;
  };

  return (
    <div className='editor-container'>
      <div className='text-input-area'>
        <div className='format-toolbar'>
          <button onClick={() => toggleFormat('bold')}>Bold</button>
          <button onClick={() => toggleFormat('italic')}>Italic</button>
          <button onClick={() => toggleFormat('underline')}>Underline</button>
        </div>
        <textarea className='textarea' value={text} onChange={(e) => setText(e.target.value)}></textarea>
      </div>
      <div className='text-preview-area'>
        {formattedText()}
      </div>
    </div>
  );
};

export default TextEditor;

