import React, { useState, useEffect } from 'react';

const CurrencyConverter = () => {
  const [currency, setCurrency] = useState('USD');
  const [amount, setAmount] = useState('');
  const [convertedAmount, setConvertedAmount] = useState('');
  const [rateInfo, setRateInfo] = useState({ rate: 0, updatedTime: '' });

  const handleCurrencyChange = (e) => {
    setCurrency(e.target.value);
    fetchRates(e.target.value);
  };

  const handleAmountChange = (e) => {
    const amt = e.target.value;
    setAmount(amt);
    setConvertedAmount(amt * rateInfo.rate);
  };

  const fetchRates = async (currency) => {
    const response = await fetch(`/api/rate/${currency}`);
    const data = await response.json();
    setRateInfo({ rate: data.rate, updatedTime: new Date().toLocaleTimeString() });
  };

  useEffect(() => {
    fetchRates(currency);
  }, [currency]);

  return (
    <div className='container'>
      <select className='selector' value={currency} onChange={handleCurrencyChange}>
        <option value='USD'>USD</option>
        <option value='EUR'>EUR</option>
        <option value='JPY'>JPY</option>
      </select>
      <input type='number' className='input' value={amount} onChange={handleAmountChange} placeholder='Enter amount' />
      <div className='result-display'>Converted Amount: {convertedAmount}</div>
      <div className='update-info'>Rate Updated: {rateInfo.updatedTime}</div>
    </div>
  );
};

export default CurrencyConverter;

