import React, { useState, useEffect } from 'react';

const TimeManagementApp = () => {
  const [tasks, setTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState('');
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [isRunning, setIsRunning] = useState(false);

  const handleStartStop = () => {
    if (isRunning) {
      setEndTime(new Date());
      const duration = (new Date() - startTime) / 1000; // seconds
      setTasks([...tasks, { taskName: currentTask, duration }]);
      setIsRunning(false);
    } else {
      setStartTime(new Date());
      setIsRunning(true);
    }
  };

  return (
    <div className='timer-container'>
      <div className='timer'>
        <input type='text' placeholder='enter task name' value={currentTask} onChange={(e) => setCurrentTask(e.target.value)} />
        <button onClick={handleStartStop}>{isRunning ? 'pause' : 'start'}</button>
      </div>
      <div className='task-list'>
        {tasks.map((task, index) => (
          <div key={index}>{task.taskName}: {task.duration} seconds</div>
        ))}
      </div>
      <div className='chart-container'>
      </div>
    </div>
  );
};

