import React, { useState, useEffect } from 'react';

const OnlineVotingSystem = () => {
  const [candidates, setCandidates] = useState([{ name: 'candidate A', votes: 0 }, { name: 'candidate B', votes: 0 }]);
  const [selectedCandidate, setSelectedCandidate] = useState('');

  const handleVote = () => {
    setCandidates(candidates.map(candidate =>
      candidate.name === selectedCandidate ? { ...candidate, votes: candidate.votes + 1 } : candidate
    ));
  };

  return (
    <div className='container'>
      <div className='vote-list'>
        {candidates.map((candidate, index) => (
          <div key={index} className='vote-item'>
            <input type='radio' name='candidate' className='radio' onChange={() => setSelectedCandidate(candidate.name)} />
            {candidate.name}
          </div>
        ))}
      </div>
      <div className='result-section'>
        {candidates.map((candidate, index) => (
          <p key={index}>{candidate.name}{candidate.votes} votes</p>
        ))}
      </div>
      <button className='vote-button' onClick={handleVote}>submit vote</button>
    </div>
  );
};

export default OnlineVotingSystem;

