import React, { useState } from 'react';

const NetworkSpeedTest = () => {
  const [speed, setSpeed] = useState({ download: '0 Mbps', upload: '0 Mbps' });

  const handleTest = async () => {
    const simulatedDownloadSpeed = (Math.random() * 100).toFixed(2) + ' Mbps';
    const simulatedUploadSpeed = (Math.random() * 50).toFixed(2) + ' Mbps';
    setSpeed({ download: simulatedDownloadSpeed, upload: simulatedUploadSpeed });
  };

  return (
    <div className='container'>
      <div className='speed-display'>
        download speed: {speed.download}<br/>
        upload speed: {speed.upload}
      </div>
      <button className='test-button' onClick={handleTest}>test internet speed</button>
    </div>
  );
};

export default NetworkSpeedTest;

