import React, { useState } from 'react';

const EnergyTracker = () => {
  const [energyData, setEnergyData] = useState({ electricity: '', water: '' });
  const [history, setHistory] = useState([]);
  const [advice, setAdvice] = useState('');

  const handleSubmit = () => {
    const newHistory = [...history, energyData];
    setHistory(newHistory);
    generateAdvice(energyData);
    setEnergyData({ electricity: '', water: '' });
  };

  const generateAdvice = (data) => {
    if (data.electricity > 100) {
      setAdvice('Try to reduce electricity consumption, for example, by using energy-saving bulbs.');
    } else {
      setAdvice('Energy usage efficiency is good, please keep it up!');
    }
  };

  return (
    <div className='container'>
      <div className='input-section'>
        <input type='number' className='input-field' placeholder='Enter electricity consumption (kWh)' value={energyData.electricity} onChange={e => setEnergyData({ ...energyData, electricity: e.target.value })} />
        <input type='number' className='input-field' placeholder='Enter water consumption (liters)' value={energyData.water} onChange={e => setEnergyData({ ...energyData, water: e.target.value })} />
        <button className='submit-button' onClick={handleSubmit}>Submit Data</button>
      </div>
      <div className='data-section'>
      </div>
      <div className='advice-section'>
        <p>{advice}</p>
      </div>
    </div>
  );
};

export default EnergyTracker;

