import React, { useState } from 'react';

const questions = [
  { question: 'Who was the first mayor of our town?', answers: ['John Doe', 'Jane Smith', 'Emily Johnson'], correct: 1 },
  { question: 'What year was the local library built?', answers: ['1920', '1950', '1980'], correct: 0 },
  { question: 'Which local landmark is over 100 years old?', answers: ['The town hall', 'The old bridge', 'Both'], correct: 2 }
];

const CulturalQuiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswer = (index) => {
    if (index === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <div className='quiz-container'>
      {showScore ? (
        <div>You scored {score} out of {questions.length}</div>
      ) : (
        <>
          <div className='question'>{questions[currentQuestion].question}</div>
          <ul className='answers'>
            {questions[currentQuestion].answers.map((answer, index) => (
              <li key={index}>
                <button className='answer' onClick={() => handleAnswer(index)}>{answer}</button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default CulturalQuiz;

