import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const FinancialDashboard = () => {
  const [stocks, setStocks] = useState([{ name: 'AAPL', price: 150.10 }, { name: 'GOOGL', price: 2735.20 }]);
  const [news, setNews] = useState(['Market opens mixed amid economic uncertainty', 'Tech stocks rally on earnings beat']);

  useEffect(() => {
    // Simulate real-time stock price updates
    const interval = setInterval(() => {
      setStocks(stocks.map(stock => ({ ...stock, price: stock.price * (1 + Math.random() * 0.005 - 0.0025) })));
    }, 5000);
    return () => clearInterval(interval);
  }, [stocks]);

  return (
    <div className='container'>
      <div className='ticker-section'>
        {stocks.map((stock, index) => <div key={index}>{stock.name}: ${stock.price.toFixed(2)}</div>)}
      </div>
      <div className='chart-section'>
        <ResponsiveContainer width='100%' height={300}>
          <LineChart data={stocks}>
            <Line type='monotone' dataKey='price' stroke='#8884d8' />
            <CartesianGrid stroke='#ccc' />
            <XAxis dataKey='name' />
            <YAxis />
            <Tooltip />
          </LineChart>
        </ResponsiveContainer>
      </div>
      <div className='news-feed'>
        {news.map((item, index) => <div key={index}>{item}</div>)}
      </div>
    </div>
  );
};

export default FinancialDashboard;

