import React, { useState } from 'react';

const ProjectDashboard = () => {
  const [tasks, setTasks] = useState([{ id: 1, name: 'API Development', status: 'ToDo' }, { id: 2, name: 'UI Mockup', status: 'InProgress' }]);
  const [sprints, setSprints] = useState([{ id: 1, name: 'Sprint 1', tasks: [1, 2] }]);
  const [messages, setMessages] = useState(['Welcome to the project chat!']);

  const handleTaskStatusChange = (taskId, status) => {
    setTasks(tasks.map(task => task.id === taskId ? { ...task, status } : task));
  };

  const handleSendMessage = (message) => {
    setMessages([...messages, message]);
  };

  return (
    <div className='container'>
      <div className='kanban-board'>
        {tasks.map((task, index) => <div key={index}>{task.name}: {task.status}</div>)}
      </div>
      <div className='sprint-planner'>
        {sprints.map((sprint, index) => <div key={index}>{sprint.name}</div>)}
      </div>
      <div className='chat-interface'>
        {messages.map((msg, index) => <div key={index}>{msg}</div>)}
        <input type='text' onKeyPress={(e) => e.key === 'Enter' ? handleSendMessage(e.target.value) : null} />
      </div>
    </div>
  );
};

export default ProjectDashboard;

