import React, { useState, useEffect } from 'react';

const BudgetManagement = () => {
  const [expenses, setExpenses] = useState([
    { category: 'Food', amount: 50 },
    { category: 'Travel', amount: 100 }
  ]);
  const [limits, setLimits] = useState({
    Food: 200,
    Travel: 300,
    Utilities: 150
  });
  const [availableBudget, setAvailableBudget] = useState(1000);

  useEffect(() => {
    const totalSpent = expenses.reduce((sum, expense) => sum + expense.amount, 0);
    setAvailableBudget(1000 - totalSpent);
  }, [expenses]);

  const addExpense = (category, amount) => {
    setExpenses([...expenses, { category, amount }]);
  };

  return (
    <div className='container'>
      <div className='ledger'>
        <h2>Shared Expense Ledger</h2>
        {expenses.map((expense, index) => (
          <div key={index}>{expense.category}: ${expense.amount}</div>
        ))}
        <button onClick={() => addExpense('Food', 20)}>Add $20 to Food</button>
        <button onClick={() => addExpense('Travel', 50)}>Add $50 to Travel</button>
      </div>
      <div className='limits'>
        <h2>Category Spending Limits</h2>
        {Object.entries(limits).map(([category, limit], index) => (
          <div key={index} className='limit-item'>
            {category}: ${limit} limit, ${expenses.filter(e => e.category === category).reduce((sum, e) => sum + e.amount, 0)} spent
          </div>
        ))}
      </div>
      <div className='budget-status'>
        Available Budget: ${availableBudget}
      </div>
    </div>
  );
};

export default BudgetManagement;

