import React, { useState } from 'react';

const SkillSharingPlatform = () => {
  const [skills, setSkills] = useState([
    { name: 'Guitar Lessons', category: 'Music', rating: 4.5, available: ['2024-10-16', '2024-10-18'] },
    { name: 'JavaScript Basics', category: 'Programming', rating: 4.8, available: ['2024-10-17', '2024-10-20'] }
  ]);
  const [bookings, setBookings] = useState([]);
  const [ratings, setRatings] = useState({});

  const bookSkill = (skillName, date) => {
    setBookings([...bookings, { skillName, date }]);
  };

  const leaveRating = (skillName, newRating) => {
    const totalRatings = (ratings[skillName]?.total || 0) + 1;
    const avgRating = ((ratings[skillName]?.rating || 0) * (totalRatings - 1) + newRating) / totalRatings;
    setRatings({ ...ratings, [skillName]: { rating: avgRating, total: totalRatings } });
  };

  return (
    <div className='container'>
      <div className='marketplace'>
        <h2>Skill Marketplace</h2>
        {skills.map((skill, index) => (
          <div key={index} className='skill-item'>
            {skill.name} ({skill.category}) - Rating: {ratings[skill.name]?.rating || skill.rating}
            <button onClick={() => bookSkill(skill.name, skill.available[0])}>Book {skill.available[0]}</button>
            <button onClick={() => leaveRating(skill.name, 5)}>Rate 5 Stars</button>
          </div>
        ))}
      </div>
      <div className='scheduler'>
        <h2>Booking Scheduler</h2>
        {bookings.map((booking, index) => (
          <div key={index}>{booking.skillName} booked for {booking.date}</div>
        ))}
      </div>
      <div className='rating'>
        <h2>Ratings</h2>
        {Object.entries(ratings).map(([skillName, { rating, total }], index) => (
          <div key={index}>{skillName}: {rating.toFixed(1)} (from {total} ratings)</div>
        ))}
      </div>
    </div>
  );
};

export default SkillSharingPlatform;

