import React, { useState, useEffect } from 'react';

const GreenhouseMonitor = () => {
  const [environment, setEnvironment] = useState({ temperature: 22, humidity: 50, lightLevel: 200 });
  const [plantHealth, setPlantHealth] = useState([{ plantId: '001', moisture: 40, nutrient: 'Good', growth: 7.5 }, { plantId: '002', moisture: 35, nutrient: 'Low', growth: 5.8 }]);
  const [automationRules, setAutomationRules] = useState([]);

  const handleEnvironmentChange = (param, value) => {
    setEnvironment({ ...environment, [param]: value });
  };

  const handleAddRule = (rule) => {
    setAutomationRules([...automationRules, rule]);
  };

  return (
    <div className='container'>
      <div className='controls-section'>
        <h3>Environmental Controls</h3>
        <button onClick={() => handleEnvironmentChange('temperature', environment.temperature + 1)}>Increase Temperature</button>
        <button onClick={() => handleEnvironmentChange('humidity', environment.humidity + 5)}>Increase Humidity</button>
        <button onClick={() => handleEnvironmentChange('lightLevel', environment.lightLevel + 20)}>Increase Light</button>
      </div>
      <div className='monitoring-section'>
        <h3>Plant Health Monitoring</h3>
        {plantHealth.map((plant, index) => <div key={index} className='sensor-item'>{`Plant ${plant.plantId} - Moisture: ${plant.moisture}% - Nutrient: ${plant.nutrient} - Growth: ${plant.growth}cm`}</div>)}
      </div>
      <div className='settings-section'>
        <h3>Automation Settings</h3>
        <button onClick={() => handleAddRule('New Rule')}>Add Rule</button>
        {automationRules.map((rule, index) => <div key={index}>{rule}</div>)}
      </div>
    </div>
  );
};

export default GreenhouseMonitor;

