import React, { useState, useEffect } from 'react';

const HabitTrackerApp = () => {
  const [habits, setHabits] = useState([
    { name: 'Exercise', days: [true, false, true, true, false, true, false] },
    { name: 'Read', days: [true, true, true, true, true, false, false] }
  ]);
  const [quotes, setQuotes] = useState([
    'The journey of a thousand miles begins with one step.',
    'Dont watch the clock; do what it does. Keep going.',
    'Success is the sum of small efforts, repeated day in and day out.'
  ]);
  const [streaks, setStreaks] = useState({ 'Exercise': 3, 'Read': 4 });
  const [quoteOfDay, setQuoteOfDay] = useState('');

  useEffect(() => {
    const today = new Date().getDay();
    setQuoteOfDay(quotes[today % quotes.length]);
  }, [quotes]);

  const toggleHabit = (habitName, dayIndex) => {
    setHabits(habits.map(habit => habit.name === habitName ? {
      ...habit,
      days: habit.days.map((completed, index) => index === dayIndex ? !completed : completed)
    } : habit));
  };

  return (
    <div className='container'>
      <div className='habit-tracker'>
        <h2>Daily Habit Tracker</h2>
        {habits.map((habit, index) => (
          <div key={index} className='habit-item'>
            <span>{habit.name}</span>
            {habit.days.map((completed, dayIndex) => (
              <button key={dayIndex} onClick={() => toggleHabit(habit.name, dayIndex)} style={{ backgroundColor: completed ? '#81c784' : '#e0e0e0' }}>
                {completed ? '' : ''}
              </button>
            ))}
          </div>
        ))}
      </div>
      <div className='streak-counter'>
        <h2>Streak Counter</h2>
        {Object.entries(streaks).map(([habitName, streak], index) => (
          <div key={index}>{habitName}: {streak} days</div>
        ))}
      </div>
      <div className='motivation'>
        <h2>Motivational Quote of the Day</h2>
        <p>{quoteOfDay}</p>
      </div>
    </div>
  );
};

export default HabitTrackerApp;

