import React, { useState } from 'react';

const StoryCollabApp = () => {
  const [story, setStory] = useState('Once upon a time, in a faraway land...');
  const [branches, setBranches] = useState([
    { text: 'The hero embarks on a journey to find the lost treasure.', votes: 5 },
    { text: 'The hero decides to protect the village from invaders.', votes: 8 }
  ]);
  const [votes, setVotes] = useState({});

  const updateStory = (newText) => {
    setStory(story + ' ' + newText);
  };

  const voteForBranch = (index) => {
    const newBranches = [...branches];
    newBranches[index].votes += 1;
    setBranches(newBranches);
  };

  return (
    <div className='container'>
      <div className='editor'>
        <h2>Live Story Editor</h2>
        <p>{story}</p>
        <textarea
          rows='5'
          placeholder='Write the next part...'
          onBlur={(e) => updateStory(e.target.value)}
        ></textarea>
      </div>
      <div className='branching'>
        <h2>Branching Storylines</h2>
        {branches.map((branch, index) => (
          <div key={index}>
            {branch.text} - Votes: {branch.votes}
            <button onClick={() => voteForBranch(index)}>Vote</button>
          </div>
        ))}
      </div>
      <div className='vote'>
        <h2>Vote for the Next Part of the Story</h2>
        {branches.map((branch, index) => (
          <div key={index}>{branch.text} - {branch.votes} votes</div>
        ))}
      </div>
    </div>
  );
};

export default StoryCollabApp;

