import React, { useState } from 'react';

const WorkoutApp = () => {
  const [workoutPlan, setWorkoutPlan] = useState([
    { name: 'Push-Ups', type: 'Strength', sets: 3, reps: 15 },
    { name: 'Running', type: 'Cardio', duration: 30, distance: 5 }
  ]);
  const [workoutLog, setWorkoutLog] = useState([]);
  const [progress, setProgress] = useState([
    { date: '2024-10-01', workout: 'Push-Ups', sets: 3, reps: 15 },
    { date: '2024-10-02', workout: 'Running', distance: 5, duration: 30 }
  ]);

  const addWorkoutLog = (workout) => {
    setWorkoutLog([...workoutLog, workout]);
  };

  return (
    <div className='container'>
      <div className='planner'>
        <h2>Custom Workout Planner</h2>
        {workoutPlan.map((exercise, index) => (
          <div key={index}>
            {exercise.name} - {exercise.type} {exercise.sets ? `- ${exercise.sets} sets of ${exercise.reps}` : `- ${exercise.duration} mins / ${exercise.distance} km`}
          </div>
        ))}
      </div>
      <div className='tracker'>
        <h2>Progress Tracker</h2>
        {progress.map((entry, index) => (
          <div key={index}>{entry.date}: {entry.workout} - {entry.sets ? `${entry.sets} sets of ${entry.reps}` : `${entry.distance} km in ${entry.duration} mins`}</div>
        ))}
      </div>
      <div className='log'>
        <h2>Workout Log</h2>
        {workoutLog.map((log, index) => (
          <div key={index}>{log.date} - {log.workout} - {log.details}</div>
        ))}
        <button onClick={() => addWorkoutLog({ date: '2024-10-10', workout: 'Push-Ups', details: '3 sets of 20 reps' })}>Log Workout</button>
      </div>
    </div>
  );
};

