component:
import React, { useState, useEffect } from 'react';

const BrainstormingTool = () => {
  const [ideas, setIdeas] = useState([
    { text: 'Create a mobile app for productivity', votes: 5 },
    { text: 'Develop a web platform for freelancers', votes: 7 }
  ]);
  const [timer, setTimer] = useState(300); // 5 minutes timer
  const [votingEnabled, setVotingEnabled] = useState(true);

  useEffect(() => {
    if (timer > 0) {
      const countdown = setInterval(() => setTimer(timer - 1), 1000);
      return () => clearInterval(countdown);
    } else {
      setVotingEnabled(false);
    }
  }, [timer]);

  const addIdea = (text) => {
    setIdeas([...ideas, { text, votes: 0 }]);
  };

  const voteIdea = (index) => {
    if (!votingEnabled) return;
    const newIdeas = [...ideas];
    newIdeas[index].votes += 1;
    setIdeas(newIdeas);
  };

  return (
    <div className='container'>
      <div className='timer'>Time Remaining: {Math.floor(timer / 60)}:{('0' + (timer % 60)).slice(-2)}</div>
      <div className='board'>
        <h2>Idea Submission Board</h2>
        {ideas.map((idea, index) => (
          <div key={index} className='idea-item'>
            {idea.text}
            <button onClick={() => voteIdea(index)} disabled={!votingEnabled}>Vote ({idea.votes})</button>
          </div>
        ))}
        <input type='text' placeholder='Submit new idea' onKeyPress={(e) => e.key === 'Enter' && addIdea(e.target.value)} />
      </div>
      <div className='voting'>
        <h2>Vote for Your Favorite Ideas</h2>
        {votingEnabled ? <p>Voting is open, cast your votes!</p> : <p>Voting has ended.</p>}
      </div>
    </div>
  );
};

export default BrainstormingTool;

