import React, { useState, useEffect } from 'react';

const TaskManagementApp = () => {
  const [tasks, setTasks] = useState({
    todo: [
      { id: 1, text: 'Design homepage', priority: 'High' },
      { id: 2, text: 'Write API documentation', priority: 'Medium' }
    ],
    inProgress: [],
    completed: []
  });
  const [notifications, setNotifications] = useState(['You have a new task assigned!', 'Task "Design homepage" has been approved.']);

  const moveTask = (taskId, from, to) => {
    const taskToMove = tasks[from].find(task => task.id === taskId);
    setTasks({
      ...tasks,
      [from]: tasks[from].filter(task => task.id !== taskId),
      [to]: [...tasks[to], taskToMove]
    });
  };

  return (
    <div className='container'>
      <div className='task-board'>
        <div className='column'>
          <h3>To Do</h3>
          {tasks.todo.map(task => (
            <div key={task.id} className='task-item'>
              {task.text} ({task.priority})
              <button onClick={() => moveTask(task.id, 'todo', 'inProgress')}>Start</button>
            </div>
          ))}
        </div>
        <div className='column'>
          <h3>In Progress</h3>
          {tasks.inProgress.map(task => (
            <div key={task.id} className='task-item'>
              {task.text} ({task.priority})
              <button onClick={() => moveTask(task.id, 'inProgress', 'completed')}>Complete</button>
            </div>
          ))}
        </div>
        <div className='column'>
          <h3>Completed</h3>
          {tasks.completed.map(task => (
            <div key={task.id} className='task-item'>
              {task.text} ({task.priority})
            </div>
          ))}
        </div>
      </div>
      <div className='notifications'>
        <h2>Notifications</h2>
        {notifications.map((note, index) => (
          <div key={index}>{note}</div>
        ))}
      </div>
    </div>
  );
};

export default TaskManagementApp;

