component:
import React, { useState, useEffect } from 'react';

const FinanceManager = () => {
  const [budget, setBudget] = useState({
    income: 3000,
    categories: {
      groceries: 400,
      utilities: 200,
      entertainment: 150,
      savings: 500
    }
  });
  const [expenses, setExpenses] = useState([
    { category: 'groceries', amount: 50, date: '2024-10-10' },
    { category: 'entertainment', amount: 30, date: '2024-10-11' }
  ]);
  const [savingsGoal, setSavingsGoal] = useState({ goal: 5000, saved: 1200 });

  const addExpense = (category, amount) => {
    setExpenses([...expenses, { category, amount, date: new Date().toISOString().split('T')[0] }]);
  };

  const calculateProgress = () => {
    return Math.min((savingsGoal.saved / savingsGoal.goal) * 100, 100).toFixed(0);
  };

  return (
    <div className='container'>
      <div className='budget-planner'>
        <h2>Monthly Budget Planner</h2>
        <div>Income: ${budget.income}</div>
        {Object.entries(budget.categories).map(([category, amount], index) => (
          <div key={index}>{category}: ${amount}</div>
        ))}
      </div>
      <div className='expense-tracker'>
        <h2>Expense Tracker</h2>
        {expenses.map((expense, index) => (
          <div key={index}>{expense.date} - {expense.category}: ${expense.amount}</div>
        ))}
        <button onClick={() => addExpense('groceries', 25)}>Add $25 to Groceries</button>
      </div>
      <div className='savings-goal'>
        <h2>Savings Goal</h2>
        <div className='progress-bar'>{calculateProgress()}%</div>
        <p>Goal: ${savingsGoal.goal}</p>
        <p>Saved: ${savingsGoal.saved}</p>
      </div>
    </div>
  );
};

export default FinanceManager;

