import React, { useState } from 'react';

const ReadingTrackerApp = () => {
  const [library, setLibrary] = useState([
    { title: '1984', status: 'Reading', pages: 328, pagesRead: 120 },
    { title: 'Brave New World', status: 'Completed', pages: 268, pagesRead: 268 },
    { title: 'To Kill a Mockingbird', status: 'Want to Read', pages: 281, pagesRead: 0 }
  ]);
  const [schedule, setSchedule] = useState({ targetDate: '2024-12-01', dailyGoal: 20 });

  const updateProgress = (title, pagesRead) => {
    setLibrary(library.map(book => 
      book.title === title ? { ...book, pagesRead: Math.min(book.pages, book.pagesRead + pagesRead) } : book));
  };

  const adjustDailyGoal = (newGoal) => {
    setSchedule({ ...schedule, dailyGoal: newGoal });
  };

  return (
    <div className='container'>
      <div className='library'>
        <h2>My Book Library</h2>
        {library.map((book, index) => (
          <div key={index} className='book-item'>
            {book.title} - {book.status} - {book.pagesRead}/{book.pages} pages read
            {book.status === 'Reading' && <button onClick={() => updateProgress(book.title, 10)}>Read 10 more pages</button>}
          </div>
        ))}
      </div>
      <div className='progress-dashboard'>
        <h2>Reading Progress Dashboard</h2>
        <p>You are reading {library[0].title} and have read {library[0].pagesRead} of {library[0].pages} pages.</p>
        <p>Based on your daily goal of {schedule.dailyGoal} pages, you will finish by {schedule.targetDate}.</p>
      </div>
      <div className='schedule'>
        <h2>Personalized Reading Schedule</h2>
        <p>Current Daily Goal: {schedule.dailyGoal} pages</p>
        <button onClick={() => adjustDailyGoal(25)}>Increase Goal to 25 pages/day</button>
        <button onClick={() => adjustDailyGoal(15)}>Decrease Goal to 15 pages/day</button>
      </div>
    </div>
  );
};

export default ReadingTrackerApp;

