import React, { useState } from 'react';

const WorkflowAutomationApp = () => {
  const [workflow, setWorkflow] = useState([
    { task: 'Input Data', status: 'Pending' },
    { task: 'Validate Data', status: 'In Progress' },
    { task: 'Send Notification', status: 'Not Started' }
  ]);
  const [rules, setRules] = useState([
    { condition: 'If task delayed', action: 'Notify Manager', triggered: false }
  ]);
  const [executions, setExecutions] = useState([
    { workflow: 'Data Processing', status: 'Running', activeTask: 'Validate Data' }
  ]);

  const updateWorkflowStatus = (index, status) => {
    setWorkflow(workflow.map((task, i) =>
      i === index ? { ...task, status } : task
    ));
  };

  return (
    <div className='container'>
      <div className='workflow-builder'>
        <h2>Workflow Builder</h2>
        {workflow.map((task, index) => (
          <div key={index} className='task-item'>
            {task.task} - {task.status}
            <button onClick={() => updateWorkflowStatus(index, 'Completed')}>Mark as Completed</button>
          </div>
        ))}
      </div>
      <div className='rule-editor'>
        <h2>Automation Rule Editor</h2>
        {rules.map((rule, index) => (
          <div key={index}>{rule.condition} - {rule.action}</div>
        ))}
      </div>
      <div className='execution-dashboard'>
        <h2>Task Execution Dashboard</h2>
        {executions.map((execution, index) => (
          <div key={index}>{execution.workflow} - {execution.status} (Active Task: {execution.activeTask})</div>
        ))}
      </div>
    </div>
  );
};

export default WorkflowAutomationApp;

