import React, { useState } from 'react';

const TravelPlannerApp = () => {
  const [destinations, setDestinations] = useState(['New York', 'Tokyo', 'Paris']);
  const [selectedDestination, setSelectedDestination] = useState('New York');
  const [itinerary, setItinerary] = useState([
    { day: 'Day 1', event: 'Arrive and check-in at hotel', cost: 150 },
    { day: 'Day 2', event: 'City tour and museum visit', cost: 100 }
  ]);
  const [budget, setBudget] = useState(itinerary.reduce((acc, item) => acc + item.cost, 0));

  const addEvent = (newEvent) => {
    const updatedItinerary = [...itinerary, newEvent];
    setItinerary(updatedItinerary);
    setBudget(updatedItinerary.reduce((acc, item) => acc + item.cost, 0));
  };

  return (
    <div className='container'>
      <div className='destination-selector'>
        <h2>Select Destination</h2>
        <select value={selectedDestination} onChange={(e) => setSelectedDestination(e.target.value)}>
          {destinations.map((destination, index) => (
            <option key={index} value={destination}>{destination}</option>
          ))}
        </select>
      </div>
      <div className='timeline'>
        <h2>Itinerary Timeline</h2>
        {itinerary.map((item, index) => (
          <div key={index} className='timeline-event'>
            {item.day} - {item.event} - ${item.cost}
          </div>
        ))}
        <button onClick={() => addEvent({ day: 'Day 3', event: 'Beach day', cost: 50 })}>Add Event</button>
      </div>
      <div className='budget-overview'>
        <h2>Budget Overview</h2>
        <div>Total Trip Cost: <span className='total-budget'>${budget}</span></div>
      </div>
    </div>
  );
};

export default TravelPlannerApp;

