import React, { useState } from 'react';

const ProjectManagementApp = () => {
  const [activityFeed, setActivityFeed] = useState([
    'Alice completed the task: Design Wireframes',
    'Bob started the task: API Integration'
  ]);
  const [kanban, setKanban] = useState({
    todo: ['Task 1: Initial Setup', 'Task 2: Research Competitors'],
    inProgress: ['Task 3: Develop Landing Page'],
    completed: ['Task 4: Create Prototype']
  });
  const [milestoneProgress, setMilestoneProgress] = useState(50);

  const moveTask = (from, to, taskIndex) => {
    const task = kanban[from][taskIndex];
    const updatedFrom = kanban[from].filter((_, index) => index !== taskIndex);
    const updatedTo = [...kanban[to], task];
    setKanban({ ...kanban, [from]: updatedFrom, [to]: updatedTo });
    if (to === 'completed') setMilestoneProgress(milestoneProgress + 10);
  };

  return (
    <div className='container'>
      <div className='activity-feed'>
        <h2>Team Activity Feed</h2>
        {activityFeed.map((activity, index) => (
          <div key={index}>{activity}</div>
        ))}
      </div>
      <div className='kanban-board'>
        <div className='kanban-column'>
          <h3>To Do</h3>
          {kanban.todo.map((task, index) => (
            <div key={index} className='task-card' onClick={() => moveTask('todo', 'inProgress', index)}>
              {task}
            </div>
          ))}
        </div>
        <div className='kanban-column'>
          <h3>In Progress</h3>
          {kanban.inProgress.map((task, index) => (
            <div key={index} className='task-card' onClick={() => moveTask('inProgress', 'completed', index)}>
              {task}
            </div>
          ))}
        </div>
        <div className='kanban-column'>
          <h3>Completed</h3>
          {kanban.completed.map((task, index) => (
            <div key={index} className='task-card'>{task}</div>
          ))}
        </div>
      </div>
      <div className='milestone-tracker'>
        <h2>Project Milestone Tracker</h2>
        <div className='progress-bar'>
          <div className='progress' style={{ width: `${milestoneProgress}%` }}>{milestoneProgress}%</div>
        </div>
      </div>
    </div>
  );
};

export default ProjectManagementApp;

