import React, { useState } from 'react';

const TicketingPlatformApp = () => {
  const [events, setEvents] = useState([
    { name: 'Music Festival', date: '2024-06-15', price: 120, availableTickets: 50 },
    { name: 'Tech Conference', date: '2024-07-10', price: 200, availableTickets: 30 },
    { name: 'Art Exhibition', date: '2024-05-25', price: 50, availableTickets: 80 }
  ]);
  const [selectedEvent, setSelectedEvent] = useState(events[0].name);
  const [tickets, setTickets] = useState(1);
  const [revenue, setRevenue] = useState(0);
  const [ticketsSold, setTicketsSold] = useState(0);

  const purchaseTickets = () => {
    const event = events.find((e) => e.name === selectedEvent);
    const updatedEvents = events.map((e) =>
      e.name === selectedEvent ? { ...e, availableTickets: e.availableTickets - tickets } : e
    );
    setEvents(updatedEvents);
    setRevenue(revenue + tickets * event.price);
    setTicketsSold(ticketsSold + tickets);
  };

  return (
    <div className='container'>
      <div className='events-slider'>
        <h2>Featured Events</h2>
        <div className='slider'>
          {events.map((event, index) => (
            <div key={index} className='event-card'>
              <h3>{event.name}</h3>
              <p>Date: {event.date}</p>
              <p>Price: ${event.price}</p>
              <p>Tickets Available: {event.availableTickets}</p>
            </div>
          ))}
        </div>
      </div>
      <div className='purchase-form'>
        <h2>Purchase Tickets</h2>
        <div className='form-group'>
          <label>Event</label>
          <select value={selectedEvent} onChange={(e) => setSelectedEvent(e.target.value)}>
            {events.map((event, index) => (
              <option key={index} value={event.name}>{event.name}</option>
            ))}
          </select>
        </div>
        <div className='form-group'>
          <label>Number of Tickets</label>
          <input type='number' value={tickets} onChange={(e) => setTickets(Number(e.target.value))} />
        </div>
        <button onClick={purchaseTickets}>Buy Tickets</button>
      </div>
      <div className='live-sales'>
        <h2>Live Sales Dashboard</h2>
        <div className='sales-data'>
          <div className='sales-box'>
            <h3>${revenue}</h3>
            <p>Total Revenue</p>
          </div>
          <div className='sales-box'>
            <h3>{ticketsSold}</h3>
            <p>Tickets Sold</p>
          </div>
          <div className='sales-box'>
            <h3>{events.reduce((acc, event) => acc + event.availableTickets, 0)}</h3>
            <p>Tickets Available</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TicketingPlatformApp;

