.container { display: grid; grid-template-columns: 1fr 2fr; grid-gap: 30px; padding: 40px; }
.habit-tracker { background-color: #ffffff; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.1); padding: 20px; }
.habit-item { display: flex; justify-content: space-between; padding: 10px 0; border-bottom: 1px solid #ecf0f1; }
.habit-item:last-child { border-bottom: none; }
.mood-journal { background-color: #ffffff; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.1); padding: 20px; }
.mood-entry { margin-bottom: 15px; }
.mood-entry input { width: 100%; padding: 10px; border-radius: 8px; border: 1px solid #ccc; }
.progress-chart { background-color: #ffffff; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.1); padding: 20px; text-align: center; }
.progress-bar-container { width: 100%; height: 25px; background-color: #ecf0f1; border-radius: 12px; margin-bottom: 15px; }
.progress-bar { height: 100%; background-color: #2ecc71; border-radius: 12px; display: flex; justify-content: center; align-items: center; color: white; font-weight: bold; }

component:
import React, { useState } from 'react';

const WellnessDashboard = () => {
  const [habits, setHabits] = useState([
    { name: 'Meditation', completed: false },
    { name: 'Exercise', completed: true },
    { name: 'Water Intake', completed: false }
  ]);
  const [mood, setMood] = useState('Feeling good!');
  const [progress, setProgress] = useState(75);

  const toggleHabit = (index) => {
    const updatedHabits = habits.map((habit, i) =>
      i === index ? { ...habit, completed: !habit.completed } : habit
    );
    setHabits(updatedHabits);
  };

  const updateMood = (e) => {
    setMood(e.target.value);
  };

  return (
    <div className='container'>
      <div className='habit-tracker'>
        <h2>Daily Habit Tracker</h2>
        {habits.map((habit, index) => (
          <div key={index} className='habit-item'>
            <span>{habit.name}</span>
            <input type='checkbox' checked={habit.completed} onChange={() => toggleHabit(index)} />
          </div>
        ))}
      </div>
      <div className='mood-journal'>
        <h2>Mood Journal</h2>
        <div className='mood-entry'>
          <input type='text' value={mood} onChange={updateMood} placeholder='How are you feeling today?' />
        </div>
      </div>
      <div className='progress-chart'>
        <h2>Weekly Progress</h2>
        <div className='progress-bar-container'>
          <div className='progress-bar' style={{ width: `${progress}%` }}>{progress}%</div>
        </div>
        <p>{progress >= 80 ? 'Great job! Keep it up!' : 'Keep pushing, you can do better!'}</p>
      </div>
    </div>
  );
};

export default WellnessDashboard;

style:
html {font-size: 16px; background-color: #1c1c1c; color: #f0e5c9; font-family: 'Lora', serif;}
.container { display: grid; grid-template-columns: 2fr 1fr; grid-gap: 40px; padding: 40px; }
.destination-showcase { grid-column: span 2; padding: 30px; background-color: #2e2e2e; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.2); }
.showcase-item { margin-bottom: 20px; background-color: #383838; padding: 20px; border-radius: 12px; transition: transform 0.3s ease; }
.showcase-item:hover { transform: scale(1.05); }
.booking-form { padding: 30px; background-color: #2e2e2e; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.2); }
.form-group { margin-bottom: 15px; }
.form-group label { font-size: 14px; color: #c8b694; }
.form-group input, .form-group select { width: 100%; padding: 10px; border-radius: 8px; border: 1px solid #444; background-color: #3e3e3e; color: #fff; }
.availability-dashboard { padding: 30px; background-color: #2e2e2e; border-radius: 12px; box-shadow: 0 6px 20px rgba(0, 0, 0, 0.2); text-align: center; }
.availability-item { background-color: #383838; padding: 15px; border-radius: 10px; margin-bottom: 10px; }
.availability-item h4 { color: #f1c40f; }

component:
import React, { useState } from 'react';

const LuxuryTravelApp = () => {
  const [destinations, setDestinations] = useState([
    { name: 'Maldives', roomsAvailable: 5 },
    { name: 'Bora Bora', roomsAvailable: 3 },
    { name: 'Santorini', roomsAvailable: 7 }
  ]);
  const [selectedDestination, setSelectedDestination] = useState('Maldives');
  const [guests, setGuests] = useState(2);
  const [checkIn, setCheckIn] = useState('2024-05-01');
  const [checkOut, setCheckOut] = useState('2024-05-07');
  const [availableRooms, setAvailableRooms] = useState(5);
  const [specialOffers, setSpecialOffers] = useState(['Complimentary Spa', 'Private Yacht Tour']);

  const handleBooking = () => {
    const updatedDestinations = destinations.map(destination =>
      destination.name === selectedDestination ? { ...destination, roomsAvailable: destination.roomsAvailable - 1 } : destination
    );
    setDestinations(updatedDestinations);
    setAvailableRooms(updatedDestinations.find(d => d.name === selectedDestination).roomsAvailable);
  };

  return (
    <div className='container'>
      <div className='destination-showcase'>
        <h2>Luxury Destinations</h2>
        {destinations.map((destination, index) => (
          <div key={index} className='showcase-item'>
            <h3>{destination.name}</h3>
            <p>Rooms Available: {destination.roomsAvailable}</p>
          </div>
        ))}
      </div>
      <div className='booking-form'>
        <h2>Book Your Stay</h2>
        <div className='form-group'>
          <label>Destination</label>
          <select value={selectedDestination} onChange={(e) => setSelectedDestination(e.target.value)}>
            {destinations.map((destination, index) => (
              <option key={index} value={destination.name}>{destination.name}</option>
            ))}
          </select>
        </div>
        <div className='form-group'>
          <label>Check-in Date</label>
          <input type='date' value={checkIn} onChange={(e) => setCheckIn(e.target.value)} />
        </div>
        <div className='form-group'>
          <label>Check-out Date</label>
          <input type='date' value={checkOut} onChange={(e) => setCheckOut(e.target.value)} />
        </div>
        <div className='form-group'>
          <label>Number of Guests</label>
          <input type='number' value={guests} onChange={(e) => setGuests(Number(e.target.value))} />
        </div>
        <button onClick={handleBooking}>Confirm Booking</button>
      </div>
      <div className='availability-dashboard'>
        <h2>Real-Time Availability</h2>
        <div className='availability-item'>
          <h4>Rooms Left: {availableRooms}</h4>
        </div>
        <div className='availability-item'>
          <h4>Special Offers</h4>
          <ul>
            {specialOffers.map((offer, index) => <li key={index}>{offer}</li>)}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default LuxuryTravelApp;
